async function hentData() {
  try {
    let respons = await fetch('https://jsonplaceholder.typicode.com/todos/1');
    let data = await respons.json();
    console.log(data);
  } catch (e) {
    console.error('Feil:', e);
  }
}

let jsonStr = '{"navn":"Ola","alder":25}';
let obj = JSON.parse(jsonStr);
console.log(obj.navn); // Ola

let person = { navn: 'Ola', alder: 25 };
let str = JSON.stringify(person);
console.log(str); // '{"navn":"Ola","alder":25}'

let tall = [1,2,3];
tall.forEach(function(verdi, indeks) {
  console.log(indeks, verdi);
});

let tall = [1,2,3];
let dobbel = tall.map(x => x * 2);
console.log(dobbel); // [2,4,6]

let tall = [1,2,3,4];
let partall = tall.filter(x => x % 2 === 0);
console.log(partall); // [2,4]

let tall = [1,2,3,4];
let sum = tall.reduce((akk, x) => akk + x, 0);
console.log(sum); // 10

let personer = [
  { navn: 'Ola', alder: 25 },
  { navn: 'Kari', alder: 30 }
];
let funnet = personer.find(p => p.navn === 'Kari');
console.log(funnet.alder); // 30

let tall = [1,2,3];
let harPartall = tall.some(x => x % 2 === 0);
console.log(harPartall); // true

let tall = [2,4,6];
let allePartall = tall.every(x => x % 2 === 0);
console.log(allePartall); // true

let tall = [1,2,3,4];
let midten = tall.slice(1,3);
console.log(midten); // [2,3]

class Person {
  constructor(navn) {
    this.navn = navn;
  }
  siHei() {
    console.log('Hei, jeg heter ' + this.navn);
  }
}
let p = new Person('Ola');
p.siHei();

class Barn extends Person {
  constructor(navn, alder) {
    super(navn);
    this.alder = alder;
  }
}
let b = new Barn('Kari', 10);
console.log(b.navn, b.alder);
